import 'package:flutter/material.dart';

class NewsFragment extends StatelessWidget {
  const NewsFragment({Key? key}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('News Fragment'),
      ),
      body: SingleChildScrollView(
        child: Column(
          children: [
            _buildNewsItem(
              context,
              'data:image/jpeg;base64,',
              'Cerita Horor Kematian Si Tukang Gibah, Minta Makamnya Dibongkar',
              'MATI TRAGIS SAAT MENGGIBAHIN ORANG',
            ),
            _buildNewsItem(
              context,
              'https://4.bp.blogspot.com/-Pk-5_uPAoM4/VW_woG7ItHI/AAAAAAAAGrI/J1JJxqV7e3g/s1600/Perempuan%2Bramai%2Bmasuk%2Bneraka.jpg',
              'MESTI BACA !!! 8 CIRI WANITA CALON PENGHUNI NERAKA',
              'Rasulullah ﷺ bersabda: Aku melihat ke dalam neraka, maka aku lihat kebanyakan penghuninya kaum wanita, yang demikian itu disebabkan oleh kerana jarang taat kepada perintah Allah dan RasulNya serta suami mereka dan kerana banyak bersolek untuk mempamerkan kecantikan selain dari Suaminya. Lalu Rasulullah bersabda: "Aku menangis sewaktu aku diangkat ke langit, aku melihat para wanita umatku sedang disiksa di dalam api neraka."',
            ),
            _buildNewsItem(
              context,
              'https://th.bing.com/th/id/OIP.9vyQWbWS2U3pt0C8bFsdtgHaE6?w=256&h=180&c=7&r=0&o=5&pid=1.7',
              'Alasan-Alasan Mengapa Poligami Hanya Khusus Laki-Laki?',
              'Poligami dalam Islam merupakan kegiatan yang dilakukan terkhusus bagi laki-laki saja. Mengapa poligami hanya khusus laki-laki sedangkan poliandri dilarang bagi perempuan?',
            ),
          ],
        ),
      ),
    );
  }

  Widget _buildNewsItem(
      BuildContext context, String imageUrl, String title, String content) {
    return Padding(
      padding: const EdgeInsets.all(8.0),
      child: Card(
        child: InkWell(
          onTap: () {},
          child: Column(
            crossAxisAlignment: CrossAxisAlignment.start,
            children: [
              Image.network(
                imageUrl,
                height: 200,
                width: double.infinity,
                fit: BoxFit.cover,
              ),
              Padding(
                padding: const EdgeInsets.all(8.0),
                child: Column(
                  crossAxisAlignment: CrossAxisAlignment.start,
                  children: [
                    Text(
                      title,
                      style: Theme.of(context).textTheme.headline6,
                    ),
                    const SizedBox(height: 8),
                    Text(
                      content,
                      style: Theme.of(context).textTheme.bodyText2,
                      maxLines: 3,
                      overflow: TextOverflow.ellipsis,
                    ),
                  ],
                ),
              ),
            ],
          ),
        ),
      ),
    );
  }
}

